INSERT INTO `db_points` VALUES (93, '两个一维数组合成一个二维数组', 'php', '', '', '', '', '', '<xmp>\r\n		$A=array(\\"a1\\",\\"a2\\",\\"a3\\");\r\n		$B=array(\\"b1\\",\\"b2\\",\\"b3\\");\r\n		$len=count($A);\r\n		for($i=0;$i<=$len-1;$i++){\r\n			$Rs[$i][\\"A1\\"]=$A[$i];\r\n			$Rs[$i][\\"B1\\"]=$B[$i];\r\n		}\r\n		echo(\\"<pre>\\");print_r($Rs);echo(\\"</pre>\\");\r\n		****************************************************\r\n		Array\r\n		(\r\n			[0] => Array\r\n				(\r\n					[A1] => a1\r\n					[B1] => b1\r\n				)\r\n		\r\n			[1] => Array\r\n				(\r\n					[A1] => a2\r\n					[B1] => b2\r\n				)\r\n		\r\n			[2] => Array\r\n				(\r\n					[A1] => a3\r\n					[B1] => b3\r\n				)\r\n		\r\n		)		\r\n</xmp>', '2014-03-12 19:45:03', '15-10-05 11:37:00');
INSERT INTO `db_points` VALUES (94, 'save——单条保存和多条同时保存的情况2', 'php', '', '', '', '', '', '<xmp>\r\nif($act==\\''save\\''){\r\n	//图片上传\r\n	$cupload=new upload();\r\n	if($_FILES[\\"pic\\"][\\"name\\"]!=\\''\\''){\r\n		$s=$cupload->uploadfile($_FILES[\\"pic\\"]);\r\n		$picfileurl=$s[\\"new_url\\"];\r\n	}else{\r\n		$picfileurl=trim($_POST[\\"picurl_u\\"]);\r\n	}\r\n	//添加到数据库\r\n	$addmood=array();\r\n	$addmood[\\"view\\"]=0;\r\n	$addmood[\\"pic\\"]=$picfileurl;\r\n	//*** flag属性选择_checkbox ***\r\n		$Flag=array();\r\n		$t=explode(\\",\\",$_POST[\\"flagAll\\"]);\r\n		foreach($t as $k=>$v){\r\n			$t=$_POST[\\"flag_\\".$v.\\"\\"];\r\n			$Flag[]=$t;\r\n		};\r\n		$flag=implode(\\",\\",$Flag);\r\n		$addmood[\\"flag\\"]=$flag;\r\n	//------------------------flag end---\r\n		$len_field=count($field[0]);\r\n		if($_POST[\\"num\\"]){//AJAX多条（或单条）同时保存，需要模板待保存input字段和数据表字段一一对应\r\n			$addmood=array();\r\n			$num=$_POST[\\"num\\"];\r\n			for($i=0;$i<=$num-1;$i++){\r\n				for($j=1;$j<=$len_field;$j++){\r\n					$index=$i*$len_field+$j;\r\n					$addmood[$field[0][$j-1]]=$_POST[\\"addField\\"][$index-1];	\r\n					if($addmood[\\"description\\"]){$addmood[\\"description\\"]=trim($addmood[\\"description\\"]);};\r\n					if($addmood[\\"content\\"]){$addmood[\\"content\\"]=trim($addmood[\\"content\\"]);};\r\n				};\r\n				$rs_add=$sdb->addTableRecode(\\"$db_name\\",$addmood);\r\n			}\r\n		}else{//只要模板字段在数据表有对应的字段即可\r\n			for($j=1;$j<=$len_field;$j++){\r\n				$addmood[$field[0][$j-1]]=$_POST[$field[0][$j-1]];	\r\n				if($addmood[\\"description\\"]){$addmood[\\"description\\"]=trim($addmood[\\"description\\"]);};\r\n				if($addmood[\\"content\\"]){$addmood[\\"content\\"]=trim($addmood[\\"content\\"]);};\r\n			};\r\n			$rs_add=$sdb->addTableRecode(\\"$db_name\\",$addmood);\r\n		}\r\n	//if($rs_add){ //有了这判断无效？\r\n		echo \\''<script language=\\"javascript\\">alert(\\"操作成功\\");location.href=\\"?part=\\''.$part.\\''&col=\\''.$_REQUEST[\\"col\\"].\\''&act=\\''.$back_act.\\''&id=\\''.$_REQUEST[\\"id\\"].\\''&pageid=\\''.$_GET[\\"pageid\\"].\\''\\";</script>\\'';\r\n		exit;\r\n	//}\r\n} \r\n\r\n</xmp>', '2014-03-12 22:21:09', '15-10-05 11:36:52');
INSERT INTO `db_points` VALUES (95, '二维数组[index]=>([field]=>val)转变成[index]=>([index]=>val)', 'php', '', '', '', '', '', '<xmp>\r\nforeach($rslist as $k1=>$v1){\r\n	foreach($v1 as $v2){\r\n		$v1_1=implode(\\"^\\",$v1);\r\n		$v1_2=explode(\\"^\\",$v1_1);\r\n		$rsList[$k1]=$v1_2;	\r\n	}\r\n}\r\n</xmp>', '2014-05-04 14:44:41', '15-10-05 11:36:44');
INSERT INTO `db_points` VALUES (98, 'css3在不同型号手机浏览器上的兼容一览表', 'css3', 'base', '', '', '', '', '<img src=\\"sys_resource/images/css3.jpg\\" />\r\n<br><br>\r\n<xmp style=\\"font-family:微软雅黑;\\">css3总结: \r\n\r\n对于webkit内核的浏览器, 除media、text-shadow外，使用其它属性基本上要加上webkit前缀. \r\n\r\ngradient在低版本的系统中渲染效果不丰富\r\n\r\nios3.2~4.3不支持position:fixed，不推荐使用\r\n\r\nandroid2.1~2.3对动画的支持效果不丰富\r\n\r\nandroid2.1~2.3不支持translate3d，可以利用它结合media写低端android版本的css hack\r\n\r\n对于winphone的IE浏览器\r\n\r\nIE9~11支持media、box-shadow、border-radius\r\n\r\nIE9对动画不支持\r\n\r\nIE9不支持box布局\r\n\r\nIE9不支持渐变，看具体情况可适当使用IE滤镜\r\n\r\n仅支持IE9的写法：{属性：属性值9;}\r\n\r\n上述可知，在Webapp开发中，我们可以把移动设备分为低端系统(IE9、android2.1~3.0、ios3.2)和高端系统(ios4+、android4.1~4.2)，针对低端系统，采用普通方案，并使用hack兼容，针对高端系统，可以采用更丰富更炫酷的效果。移动设备更新换代的很快，目前各种手机的最新版本系统已经支持css3的大部分效果，相信在未来对css3的支持可以达成统一。\r\n</xmp>', '2014-06-20 14:00:02', '15-12-17 10:36:50');
INSERT INTO `db_points` VALUES (100, 'sys.upload.class', 'php', '', '', '', '', '', '<h3>php类sys.upload.class的调用：</h3>\r\n<xmp>cupload=new upload();\r\nif($_FILES[\\"pic\\"][\\"name\\"]!=\\''\\''){\r\n	$s=$cupload->uploadfile($_FILES[\\"pic\\"]);\r\n	$picfileurl=$s[\\"new_url\\"];\r\n}else{\r\n	$picfileurl=trim($_POST[\\"picurl_u\\"]);\r\n}\r\n</xmp>', '2014-06-20 14:22:40', '15-10-05 11:36:34');
INSERT INTO `db_points` VALUES (101, 'php  file函数', 'php', 'function', '', '', '', '', '<xmp>fopen(filename,mode,include_path,context) 函数打开文件或者 URL\r\n\r\nfwrite(file,string,length)  函数写入文件（可安全用于二进制文件）\r\n\r\nfclose(file) 函数关闭一个打开文件。\r\n\r\nfile_put_contents() 函数把一个字符串写入文件中。\r\n与依次调用 fopen()，fwrite() 以及 fclose() 功能一样。\r\n\r\nfile_exists(path) 函数检查文件或目录是否存在。\r\n\r\nmove_uploaded_file(file,newloc) 函数将上传的文件移动到新位置。\r\n\r\n*******************************\r\n$_FILES[\\"filename\\"][\\"name\\"] - 被上传文件的名称\r\n$_FILES[\\"filename\\"][\\"type\\"] - 被上传文件的类型\r\n$_FILES[\\"filename\\"][\\"size\\"] - 被上传文件的大小，以字节计\r\n$_FILES[\\"filename\\"][\\"tmp_name\\"] - 存储在服务器的文件的临时副本的名称\r\n$_FILES[\\"filename\\"][\\"error\\"] - 由文件上传导致的错误代码\r\n</xmp>', '2014-06-20 14:23:04', '14-11-23 11:35:58');
