INSERT INTO `db_term` VALUES (13, 'cmd', '概念', '', '', '', '', '', '命令提示符<br>\r\n在操作系统中，提示进行命令输入的一种工作提示符<br>\r\n在不同的操作系统环境下，命令提示符各不相同<br>\r\ncmd.exe是在windows环境下32位的命令行程序<br>\r\n<a href=\\"http://baike.baidu.com/view/756438.htm?fromtitle=CMD&fromid=1193011&type=syn\\" target=\\"_blank\\">百度定义</a>', '2015-11-15 22:20:05', '15-11-16 04:44:46');
INSERT INTO `db_term` VALUES (14, 'shell', '概念', '', '', '', '', '', '命令解析器<br>\r\n俗称壳（用来区别于核）<br>\r\n类似于DOS下的command和后来的cmd.exe<br>\r\n交互式解释和执行用户输入的命令或者自动地解释和执行预先设定好的一连串的命令<br>\r\n基本上分两大类:<br>\r\n一：图形界面shell<br>\r\n二：命令行式shell<br>\r\n它又是一种程序设计语言<br>\r\n<a href=\\"http://baike.baidu.com/item/shell/99702\\" target=\\"_blank\\">百度定义</a>', '2015-11-15 22:20:23', '15-11-16 05:30:25');
