INSERT INTO `db_term` VALUES (47, 'CommonJS', '标准/协议', '', '', '', '', '', '是javascript模块化编程的一种规范<br>\r\n提出了许多新的JavaScript架构方案和标准<br>\r\n定义很多普通应用程序（主要指非浏览器的应用）使用的API，从而填补了这个空白<br>\r\n它的终极目标是提供一个类似Python，Ruby和Java标准库<br>\r\n主要用在nodejs服务器端模块化实现<br>\r\n加载模块是同步的，只有加载完成，才能执行后面的操作<br>\r\n由于服务端的编程模块文件都存在于本地硬盘，所以加载较快,不用考虑非同步加载的方式', '2015-11-15 22:28:36', '16-01-09 04:40:05');
