INSERT INTO `db_term` VALUES (9, 'ECMA', '标准/协议', '', '', '', '', '', '欧洲计算机制造联合会<br>\r\nEuropean Computer Manufactures Association<br>\r\n主要任务是研究信息和通讯技术方面的标准并发布有关技术报告，以便促进和标准化对信息处理和电信系统的使用过程。<br>\r\n<a href=\\''http://baike.baidu.com/view/786648.htm\\'' target=\\"_blank\\">百度定义</a>\r\n<a href=\\"http://www.w3school.com.cn/htmldom/\\" target=\\"_blank\\">w3c教程</a>', '2014-09-21 23:19:56', '15-11-16 11:44:51');
INSERT INTO `db_term` VALUES (81, 'javascript', '语言', '', '', '', '', '', '是一种轻量级的编程语言,<br>\r\n是动态类型、弱类型、基于原型的直译式(代码不进行预编译)脚本语言，<br>\r\n具有跨平台特性，常用来给HTML网页增加动态功能<br>\r\n组成：ECMAScript、DOM、BOM<br>\r\n<a href=\\"http://baike.baidu.com/view/16168.htm\\" target=\\"_blank\\">百度定义</a>\r\n<a href=\\"http://www.w3school.com.cn/js/index.asp\\" target=\\"_blank\\">w3s教程</a>', '2015-11-16 14:28:17', '15-11-16 02:31:06');
INSERT INTO `db_term` VALUES (82, 'TCP', '标准/协议', '', '', '', '', '', '传输控制协议<br>\r\nTransmission Control Protocol<br>\r\n用于从应用程序到网络的数据传输控制;<br>\r\nTCP 负责在数据传送之前将它们分割为 IP 包，然后在它们到达的时候将它们重组。<br>\r\n<a href=\\"http://baike.baidu.com/subview/32754/8048820.htm\\" target=\\"_blank\\">百度定义</a>', '2015-11-16 15:21:53', NULL);
INSERT INTO `db_term` VALUES (83, 'IP', '标准/协议', '', '', '', '', '', '网际协议<br>\r\nInternet Protocol<br>\r\n负责计算机之间的通信；<br>\r\nIP 负责在因特网上发送和接收数据包。<br>\r\n<a href=\\"http://baike.baidu.com/item/IP/224599\\" target=\\"_blank\\">百度定义</a>', '2015-11-16 15:23:11', NULL);
INSERT INTO `db_term` VALUES (84, 'HTTPS', '标准/协议', '', '', '', '', '', 'Hyper Text Transfer Protocol over Secure Socket Layer<br>\r\nHTTP的安全版，即HTTP下加入SSL层<br>\r\n负责在 web 服务器和 web 浏览器之间的安全通信；<br>\r\n作为有代表性的应用，HTTPS 会用于处理信用卡交易和其他的敏感数据。<br>\r\n<a href=\\"http://baike.baidu.com/view/14121.htm\\" target=\\"_blank\\">百度定义</a>', '2015-11-16 15:26:40', NULL);
INSERT INTO `db_term` VALUES (85, 'SSL', '标准/协议', '', '', '', '', '', '安全套接层<br>\r\nSecure Sockets Layer<br>\r\n与TLS(其继任者)为网络通信提供安全及数据完整性的一种安全协议<br>\r\n用于为安全数据传输加密数据<br>\r\n<a href=\\"http://baike.baidu.com/view/16147.htm\\" target=\\"_blank\\">百度定义</a>', '2015-11-16 15:31:41', NULL);
INSERT INTO `db_term` VALUES (86, 'TLS', '标准/协议', '', '', '', '', '', '安全传输层协议<br>\r\nTransport Layer Security<br>\r\n用于在两个通信应用程序之间提供保密性和数据完整性<br>\r\n组成： TLS 记录协议（TLS Record）和 TLS 握手协议（TLS Handshake）。<br>\r\n<a href=\\"http://baike.baidu.com/view/875396.htm\\" target=\\"_blank\\">百度定义</a>', '2015-11-16 15:34:07', NULL);
INSERT INTO `db_term` VALUES (87, 'SMTP', '标准/协议', '', '', '', '', '', '简单邮件传输协议<br>\r\nSimple Mail Transfer Protocol<br>\r\n一组用于由源地址到目的地址传送邮件的规则，由它来控制信件的中转方式<br>\r\n属于TCP/IP协议簇<br>\r\n<a href=\\"http://baike.baidu.com/view/5450.htm\\" target=\\"_blank\\">百度定义</a>', '2015-11-16 15:36:58', NULL);
INSERT INTO `db_term` VALUES (88, 'MIME', '标准/协议', '', '', '', '', '', '多用途互联网邮件扩展类型<br>\r\nMultipurpose Internet Mail Extensions<br>\r\n扩展了电子邮件标准<br>\r\n使 SMTP 有能力通过 TCP/IP 网络传输多媒体文件，包括声音、视频和二进制数据<br>\r\n设定某种扩展名的文件用一种应用程序来打开的方式类型，当该扩展名文件被访问的时候，浏览器会自动使用指定应用程序来打开<br>\r\n<a href=\\"http://baike.baidu.com/view/160611.htm\\" target=\\"_blank\\">百度定义</a>', '2015-11-16 15:40:40', NULL);
INSERT INTO `db_term` VALUES (89, 'IMAP', '标准/协议', '', '', '', '', '', 'Internet邮件访问协议<br>\r\nInternet Mail Access Protocol<br>\r\n用于存储和取回电子邮件<br>\r\n主要作用是让邮件客户端可以通过这种协议从邮件服务器上获取邮件的信息，下载邮件等<br>\r\n它与POP3协议的主要区别是用户可以不用把所有的邮件全部下载，可以通过客户端直接对服务器上的邮件进行操作。<br>\r\n<a href=\\"http://baike.baidu.com/view/17877.htm\\" target=\\"_blank\\">百度定义</a>', '2015-11-16 16:16:37', NULL);
INSERT INTO `db_term` VALUES (10, 'W3C', '标准/协议', '', '', '', '', '', '万维网联盟<br>\r\n是Web技术领域最具权威和影响力的国际中立性技术标准机构,是一个会员组织;<br>\r\n致力于对web 进行标准化<br>\r\n创建并维护了 WWW 标准<br>\r\n最重要的工作是发展 Web 规范,是描述 web 通信协议（比如 HTML 和 XML）和其他构建模块的“推荐标准”。<br>\r\n<a href=\\"http://baike.baidu.com/view/80669.htm\\" target=\\"_blank\\">百度定义</a>\r\n<a href=\\"http://www.w3school.com.cn/w3c/index.asp\\" target=\\"_blank\\">w3s教程</a>', '2014-09-21 23:26:43', '15-11-16 02:42:28');
INSERT INTO `db_term` VALUES (11, 'TCP/IP', '标准/协议', '', '', '', '', '', '传输控制协议/因特网互联协议，<br>又名网络通讯协议<br>\r\nTransmission Control Protocol/Internet Protocol<br>\r\n由网络层的IP协议和传输层的TCP协议组成。<br>\r\n它定义了电子设备如何连入因特网，以及数据如何在它们之间传输的标准。<br>\r\n是对那些计算机必须遵守以便彼此通信的规则的描述。<br>\r\n<a href=\\"http://baike.baidu.com/view/7649.htm\\" target=\\"_blank\\">百度定义</a>\r\n<a href=\\"http://www.w3school.com.cn/tcpip/index.asp\\" target=\\"_blank\\">w3s教程</a>', '2014-09-21 23:33:37', '15-11-16 12:01:29');
INSERT INTO `db_term` VALUES (12, 'HTTP', '标准/协议', '', '', '', '', '', '超文本传输协议<br>\r\nHyperText Transfer Protocol<br>\r\nWWW文件都必须遵守的网络协议，<br>\r\n负责 web 服务器与 web 浏览器之间的通信<br>\r\n用于从 web 客户端（浏览器）向 web 服务器发送请求，并从 web 服务器向 web 客户端返回内容（网页）<br>\r\n<a href=\\"http://baike.baidu.com/view/9472.htm\\" target=\\"_blank\\">百度定义</a>', '2014-09-21 23:40:32', '15-11-16 03:19:03');
INSERT INTO `db_term` VALUES (80, 'ECMAScript', '标准/协议', '', '', '', '', '', '正式名称为 ECMA 262 和 ISO/IEC 16262，<br>是ECMA制定的脚本语言规范。<br>\r\n它只提供API却没有提供实现，脚本语言(如javascript)是此标准的实现和拓展。<br>\r\n<a href=\\"http://baike.baidu.com/view/810176.htm\\" target=\\"_blank\\">百度定义</a>', '2015-11-16 14:20:06', '15-11-16 02:21:41');
INSERT INTO `db_term` VALUES (90, 'POP', '标准/协议', '', '', '', '', '', '即邮局协议<br>\r\nPost Office Protocol<br>\r\n用于从电子邮件服务器向个人电脑下载电子邮件<br>\r\n<a href=\\"http://baike.baidu.com/subview/33001/6742587.htm#viewPageContent\\" target=\\"_blank\\">百度定义</a>', '2015-11-16 16:19:13', NULL);
INSERT INTO `db_term` VALUES (91, 'FTP', '标准/协议', '', '', '', '', '', '文件传输协议<br>\r\nFile Transfer Protocol<br>\r\n负责计算机之间的文件传输<br>\r\n它也是一个应用程序<br>\r\n<a href=\\"http://baike.baidu.com/subview/369/6149695.htm\\" target=\\"_blank\\">百度定义</a>', '2015-11-16 16:21:15', NULL);
INSERT INTO `db_term` VALUES (92, 'IDE', '概念', '', '', '', '', '', 'IDE<br>\r\nIntegrated Development Environment<br>\r\n集成开发环境<br>\r\n可以辅助开发程序的应用软件<br>\r\n一般包括代码编辑器、编译器、调试器和图形用户界面工具', '2016-01-05 14:41:45', NULL);
INSERT INTO `db_term` VALUES (93, 'AMD', '标准/协议', '', '', '', '', '', 'AMD<br>\r\nAsynchronous Module Definition<br>\r\n意思就是\\"异步模块定义\\"<br>\r\n是一种js的模块规范<br>\r\n统一了JavaScript的模块定义和加载机制<br>\r\n也允许输出模块兼容CommonJS规范<br>\r\nAMD 是 RequireJS 在推广过程中对模块定义的规范化产出', '2016-01-09 16:25:12', '16-01-09 04:39:45');
INSERT INTO `db_term` VALUES (94, 'CMD', '标准/协议', '', '', '', '', '', 'CMD<br>\r\nCommon Module Definition<br>\r\n模块定义规范<br>\r\n明确了模块的基本书写格式和基本交互规则<br>\r\n是 SeaJS 在推广过程中对模块定义的规范化产出<br>\r\n对于依赖的模块,CMD 是延迟执行的', '2016-01-09 16:39:16', NULL);
INSERT INTO `db_term` VALUES (95, 'MVVM', '概念', '', '', '', '', '', 'Model-View-ViewModel<br>\r\n是MVP（Model-View-Presenter）模式与WPF结合的应用方式时发展演变过来的一种新型架构框架<br>\r\n使用的是数据绑定基础架构,它们可以轻松构建UI的必要元素<br>\r\n主要目的是分离视图（View）和模型（Model），有几大优点:<br>\r\n1. 低耦合<br>\r\n2. 可重用性<br>\r\n3. 独立开发<br>\r\n4. 可测试', '2016-01-09 16:48:00', NULL);
